<?php// saiful custom comments output htmlfunction saiful_comments( $comment, $args, $depth ) {	// Get correct tag used for the comments	if ( 'div' === $args['style'] ) {		$tag       = 'div';		$add_below = 'comment single_comment';	} else {		$tag       = 'li';		$add_below = 'div-comment';	} ?>	<<?php echo esc_html( $tag ); ?> <?php comment_class( empty( $args['has_children'] ) ? 'single_comment' : 'parent single_comment' ); ?> id="comment-<?php comment_ID() ?>">	<?php	// Switch between different comment types	switch ( $comment->comment_type ) :		case 'pingback' :		case 'trackback' : ?>		<div class="pingback-entry"><span class="pingback-heading"><?php esc_html_e( 'Pingback:', 'saiful' ); ?></span> <?php comment_author_link(); ?></div>	<?php		break;		default :		if ( 'div' != $args['style'] ) { ?>			<div id="div-comment-<?php comment_ID() ?>" class="comment-body">		<?php } ?>			<div class="comment_img vcard">				<?php				// Display avatar unless size is set to 0				if ( $args['avatar_size'] != 0 ) {					$avatar_size = ! empty( $args['avatar_size'] ) ? $args['avatar_size'] : 60; // set default avatar size                                            echo get_avatar( $comment, $avatar_size );				}                                ?>			</div><!-- .comment-author -->			<div id="div-comment-<?php comment_ID() ?>" class="comment_info comment-body">                            <div class="comment_author">                                <div class="author_info">                                    <h4><?php echo get_comment_author_link(); ?></h4>                                    <p><?php echo get_comment_date(); ?></p>                                </div>                                <div class="button reply">                                    <?php                                    // Display comment reply link                                    comment_reply_link( array_merge( $args, array(                                            'add_below' => $add_below,                                            'depth'     => $depth,                                            'max_depth' => $args['max_depth'],                                            'reply_text'    => esc_html__( 'Reply','saiful' ),                                    ) ) ); ?>                                </div>                            </div>                            <div class="comment_text">                                  <?php comment_text(); ?>                            </div>				<?php				// Display comment moderation text				if ( $comment->comment_approved == '0' ) { ?>					<em class="comment-awaiting-moderation"><?php _e( 'Your comment is awaiting moderation.', 'saiful' ); ?></em><br/><?php				} ?>			</div><!-- .comment-details -->	<?php		if ( 'div' != $args['style'] ) { ?>			</div>		<?php }	// IMPORTANT: Note that we do NOT close the opening tag, WordPress does this for us		break;	endswitch; // End comment_type check.}